import React from 'react';
import styled from 'styled-components';

const FooterWrapper = styled.footer`
  display: flex;
  margin-top: 89px;
  width: 100%;
  max-width: 1145px;
  align-items: flex-start;
  gap: 20px;
  justify-content: space-between;
  padding: 0 20px;
  @media (max-width: 991px) {
    max-width: 100%;
    flex-wrap: wrap;
    margin-top: 40px;
  }
`;

const LogoSection = styled.div`
  display: flex;
  flex-direction: column;
`;

const FooterLogo = styled.img`
  aspect-ratio: 1.3;
  object-fit: auto;
  object-position: center;
  width: 245px;
  max-width: 100%;
`;

const LogoDescription = styled.p`
  font-family: Manrope, sans-serif;
  font-size: 16px;
  color: #71717a;
  font-weight: 400;
  line-height: 26px;
  margin-top: 16px;
`;

const FooterColumn = styled.div`
  display: flex;
  flex-direction: column;
`;

const ColumnTitle = styled.h4`
  color: #94a3b8;
  letter-spacing: 1px;
  font: 600 13px/138% Manrope, sans-serif;
  margin-bottom: 37px;
`;

const ColumnLink = styled.a`
  color: #090914;
  font: 500 16px/24px Manrope, sans-serif;
  text-decoration: none;
  margin-bottom: 12px;
`;

const NewsletterForm = styled.form`
  display: flex;
  flex-direction: column;
`;

const NewsletterInput = styled.input`
  font-family: Poppins, sans-serif;
  border-radius: 10px;
  border: 1px solid #e4e4e7;
  background-color: #fff;
  color: #a1a1aa;
  font-weight: 400;
  line-height: 162.5%;
  padding: 20px;
  margin-bottom: 12px;
`;

const SubscribeButton = styled.button`
  font-family: Poppins, sans-serif;
  border: none;
  border-radius: 9px;
  background-color: #2563eb;
  color: #fff;
  font-weight: 500;
  text-align: center;
  line-height: 150%;
  padding: 16px;
  cursor: pointer;
`;

function Footer() {
  return (
    <FooterWrapper>
      <LogoSection>
        <FooterLogo src="http://b.io/ext_14-" alt="Footer Logo" />
        <LogoDescription>
          Clarity gives you the blocks and components you need to create a truly professional website.
        </LogoDescription>
      </LogoSection>
      <FooterColumn>
        <ColumnTitle>Company</ColumnTitle>
        <ColumnLink href="#">About</ColumnLink>
        <ColumnLink href="#">Features</ColumnLink>
        <ColumnLink href="#">Works</ColumnLink>
        <ColumnLink href="#">Career</ColumnLink>
      </FooterColumn>
      <FooterColumn>
        <ColumnTitle>Help</ColumnTitle>
        <ColumnLink href="#">Customer Support</ColumnLink>
        <ColumnLink href="#">Delivery Details</ColumnLink>
        <ColumnLink href="#">Terms & Conditions</ColumnLink>
        <ColumnLink href="#">Privacy Policy</ColumnLink>
      </FooterColumn>
      <FooterColumn>
        <ColumnTitle>Newsletter</ColumnTitle>
        <NewsletterForm>
          <NewsletterInput type="email" placeholder="Enter your email address" />
          <SubscribeButton type="submit">Subscribe Now</SubscribeButton>
        </NewsletterForm>
      </FooterColumn>
    </FooterWrapper>
  );
}

export default Footer;
