import React from 'react';
import styled from 'styled-components';

const SectionTitle = styled.h2`
  color: #000;
  text-align: center;
  letter-spacing: -1.2px;
  align-self: center;
  margin-top: 134px;
  font: 600 40px Manrope, sans-serif;
  @media (max-width: 991px) {
    margin-top: 40px;
  }
`;

const CardGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(4, 1fr);
  gap: 20px;
  margin-top: 29px;
  width: 100%;
  max-width: 1110px;
  padding: 0 20px;
  @media (max-width: 991px) {
    grid-template-columns: 1fr;
    max-width: 100%;
  }
`;

const Card = styled.article`
  border-radius: 12px;
  background-color: #f2f2f2;
  display: flex;
  flex-direction: column;
  color: #142800;
  padding: 16px;
  @media (max-width: 991px) {
    margin-top: 30px;
  }
`;

const CardImage = styled.img`
  aspect-ratio: 1.32;
  object-fit: cover;
  width: 100%;
`;

const CardContent = styled.div`
  display: flex;
  margin-top: 16px;
  flex-direction: column;
`;

const CardTitle = styled.h3`
  font: 700 16px/150% Manrope, sans-serif;
`;

const CardDescription = styled.p`
  margin-top: 8px;
  font: 400 12px Manrope, sans-serif;
`;

function ExploreSection() {
  return (
    <section>
      <SectionTitle>Explore local species</SectionTitle>
      <CardGrid>
        {[1, 2, 3, 4].map((index) => (
          <Card key={index}>
            <CardImage src="http://b.io/ext_13-" alt={`Wildlife ${index}`} loading="lazy" />
            <CardContent>
              <CardTitle>Bird Watching: Wildlife</CardTitle>
              <CardDescription>
                Explore the diverse bird species in their natural habitats. Witness their behavior and listen to their melodious songs.
              </CardDescription>
            </CardContent>
          </Card>
        ))}
      </CardGrid>
    </section>
  );
}

export default ExploreSection;
