import React, { useState, useEffect } from 'react';
import styled from 'styled-components';

const SectionTitle = styled.h2`
  color: #000;
  text-align: center;
  letter-spacing: -1.2px;
  align-self: center;
  margin-top: 134px;
  font: 600 40px Manrope, sans-serif;
  @media (max-width: 991px) {
    margin-top: 40px;
  }
`;

const CardGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(4, 1fr);
  gap: 20px;
  margin-top: 29px;
  width: 100%;
  max-width: 1110px;
  padding: 0 20px;
  @media (max-width: 991px) {
    grid-template-columns: 1fr;
    max-width: 100%;
  }
`;

const Card = styled.article`
  border-radius: 12px;
  background-color: #f2f2f2;
  display: flex;
  flex-direction: column;
  color: #142800;
  padding: 16px;
  @media (max-width: 991px) {
    margin-top: 30px;
  }
`;

const CardImage = styled.img`
  aspect-ratio: 1.32;
  object-fit: cover;
  width: 100%;
`;

const CardContent = styled.div`
  display: flex;
  margin-top: 16px;
  flex-direction: column;
`;

const CardTitle = styled.h3`
  font: 700 16px/150% Manrope, sans-serif;
`;

const CardDescription = styled.p`
  margin-top: 8px;
  font: 400 12px Manrope, sans-serif;
`;

const cardData = [
  { 
    id: 1,
    title: 'Bird Watching: Wildlife',
    description:
      'Explore the diverse bird species in their natural habitats. Witness their behavior and listen to their melodious songs.',
    image:
      'https://cdn.builder.io/api/v1/image/assets/TEMP/6afda67fd44640083cd04203b8b7a49e2114ca96fdeacf3468259f46772559ad?apiKey=e3765372b84f44f7af794f9207eac8b0&',
  },
  { 
    id: 2,
    title: 'Bird Watching: Wildlife',
    description:
      'Explore the diverse bird species in their natural habitats. Witness their behavior and listen to their melodious songs.',
    image:
      'https://cdn.builder.io/api/v1/image/assets/TEMP/6afda67fd44640083cd04203b8b7a49e2114ca96fdeacf3468259f46772559ad?apiKey=e3765372b84f44f7af794f9207eac8b0&',
  },
  {
    id: 3,
    title: 'Bird Watching: Wildlife',
    description:
      'Explore the diverse bird species in their natural habitats. Witness their behavior and listen to their melodious songs.',
    image:
      'https://cdn.builder.io/api/v1/image/assets/TEMP/6afda67fd44640083cd04203b8b7a49e2114ca96fdeacf3468259f46772559ad?apiKey=e3765372b84f44f7af794f9207eac8b0&',
  },
  {
    id: 4,
    title: 'Bird Watching: Wildlife',
    description:
      'Explore the diverse bird species in their natural habitats. Witness their behavior and listen to their melodious songs.',
    image:
      'https://cdn.builder.io/api/v1/image/assets/TEMP/6afda67fd44640083cd04203b8b7a49e2114ca96fdeacf3468259f46772559ad?apiKey=e3765372b84f44f7af794f9207eac8b0&',
  },
];

function ExploreSection() {
  const [scrollPosition, setScrollPosition] = useState(0);
  
  useEffect(() => {
    const interval = setInterval(() => {
      setScrollPosition((prevPosition) => {
        const newPosition = prevPosition + 1;
        return newPosition >= 100 ? 0 : newPosition;
      });
    }, 50);

    return () => clearInterval(interval);
  }, []);

  return (
    <section>
      <SectionTitle>Explore local species</SectionTitle>
      <CardGrid>
        {cardData.map((card) => (
          <Card key={card.id} style={{ transform: `translateX(-${scrollPosition}%)` }}>
            <CardImage src={card.image} alt={card.title} loading="lazy" />
            <CardContent>
              <CardTitle>{card.title}</CardTitle>
              <CardDescription>{card.description}</CardDescription>
            </CardContent>
          </Card>
        ))}
      </CardGrid>
    </section>
  );
}

export default ExploreSection;
