import React from 'react';
import styled from 'styled-components';

const HeroSection = styled.section`
  display: flex;
  flex-direction: column;
  align-items: flex-start; /* Align items to the start (left) */
  text-align: left; /* Align text to the left */
  width: 100%;
  max-width: 1200px;
  margin: 0 auto;
  justify-content: center;
`;

const Card = styled.div`
  background-color: #fff;
  border-radius: 12px;
  box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
  padding:0px 35px;
  max-width: 800px;
  // width: 100%;
  text-align: left;
  @media (max-width: 991px) {
    padding: 20px;
  }
`;

const Title = styled.h1`
  color: #000;
  letter-spacing: -2.04px;
  font: 500 56px/74px Inter, sans-serif;
  margin-left: 15px; /* Add left margin */
  @media (max-width: 991px) {
    max-width: 100%;
    font-size: 40px;
    line-height: 59px;
    margin-left: 0; /* Remove left margin for smaller screens */
  }
`;

const HighlightedText = styled.span`
  color: #78f279;
`;

const Description = styled.p`
  color: #162407;
  letter-spacing: -0.26px;
  margin-top: 16px;
  font: 400 20px Manrope, sans-serif;
  max-width: 600px;
  margin-left: 15px; /* Add left margin */
  @media (max-width: 991px) {
    max-width: 100%;
    margin-left: 0; /* Remove left margin for smaller screens */
  }
`;

const ButtonGroup = styled.div`
  display: flex;
  margin-top: 32px;
  gap: 16px;
  font-size: 13px;
  color: #142800;
  font-weight: 700;
  line-height: 150%;
  margin-left: 15px; /* Add left margin */
  @media (max-width: 991px) {
    margin-left: 0; /* Remove left margin for smaller screens */
  }
`;

const PrimaryButton = styled.button`
  font-family: Manrope, sans-serif;
  justify-content: center;
  align-items: center;
  border: none;
  cursor: pointer;
  border-radius: 40px;
  background-color: #78f279;
  padding: 12px 32px;
  @media (max-width: 991px) {
    padding: 12px 20px;
  }
`;

const SecondaryButton = styled.button`
  font-family: Manrope, sans-serif;
  background: none;
  border: none;
  cursor: pointer;
  padding: 0;
  color: inherit;
`;

function Hero() {
  return (
    <HeroSection>
      <Card>
        <Title>
          Add your <HighlightedText>specimen</HighlightedText>
        </Title>
        <Description>
          At Clever, we securely connect the data and applications that schools rely on to make digital learning work.
        </Description>
        <ButtonGroup>
          <PrimaryButton>Flora</PrimaryButton>
          <SecondaryButton>Learn more</SecondaryButton>
        </ButtonGroup>
      </Card>
    </HeroSection>
  );
}

export default Hero;
