import React from 'react';
import styled from 'styled-components';

const HeroSection = styled.section`
  display: flex;
  width: 584px;
  max-width: 100%;
  flex-direction: column;
  margin: 57px 0 0 0px;
  @media (max-width: 991px) {
    margin-top: 40px;
  }
`;

const Title = styled.h1`
  color: #78f279;
  letter-spacing: -2.04px;
  font: 500 56px/74px Inter, sans-serif;
  @media (max-width: 991px) {
    max-width: 100%;
    font-size: 40px;
    line-height: 59px;
  }
`;

const Description = styled.p`
  color: #162407;
  letter-spacing: -0.26px;
  margin-top: 16px;
  font: 400 20px Manrope, sans-serif;
  @media (max-width: 991px) {
    max-width: 100%;
  }
`;

const ButtonGroup = styled.div`
  display: flex;
  margin-top: 32px;
  width: 290px;
  max-width: 100%;
  gap: 16px;
  font-size: 13px;
  color: #142800;
  font-weight: 700;
  line-height: 150%;
`;

const PrimaryButton = styled.button`
  font-family: Manrope, sans-serif;
  justify-content: center;
  align-items: center;
  border: none;
  cursor: pointer;
  border-radius: 40px;
  background-color: #78f279;
  padding: 12px 16px;
  @media (max-width: 991px) {
    padding: 12px 20px;
  }
`;

const SecondaryButton = styled.button`
  font-family: Manrope, sans-serif;
  background: none;
  border: none;
  cursor: pointer;
  padding: 0;
  color: inherit;
`;

function Hero() {
  return (
    <HeroSection>
      <Title>
        Add your <span style={{ color: '#78f279' }}>specimen</span>
      </Title>
      <Description>
        At Clever, we securely connect the data and applications that schools
        rely on to make digital learning work
      </Description>
      <ButtonGroup>
        <PrimaryButton>Flora</PrimaryButton>
        <SecondaryButton>Learn more</SecondaryButton>
      </ButtonGroup>
    </HeroSection>
  );
}

export default Hero;
